import axios from "axios"
import React,{ useEffect, useState } from "react";
import './static/View.css'
import ViewLayout from "./ViewLayout";
const View = (props) =>{
    const [Data,SetData] = useState([])
    const [Filtercateg,Setfiltercateg] = useState("")
    const [Subforcat,SetSubforcat] = useState("")
    const [Desc,SetDesc] = useState(true)
    async function fetchProduct(){
        const response = await axios.get("http://localhost:5000/view/view_product");
        SetData(response.data)
    }
    useEffect(()=>{
        fetchProduct()
    },[])

    const getabstract = (urls,titles) =>{
        fetch(urls).then((response)=>{
            response.blob().then((blob)=>{
                const fileURL = window.URL.createObjectURL(blob);
                let alink = document.createElement("a");
                alink.href = fileURL;
                alink.download = titles + " abstract";
                alink.click();
            })
        })
    }
    const changecateg =(categ)=>{
        SetSubforcat("")
        Setfiltercateg(categ)
    }
    const changesubcat =(subcateg) =>{
        SetSubforcat(subcateg)
    }
    const filtering = (product) =>{
        return Filtercateg === "" ? 
    product.category.length > 0 : 
    (Subforcat === "" ? product.category === Filtercateg : product.sub_category === Subforcat);
    }
    const Projects = Data.filter(filtering)
    
    const subcatfilter = (data) =>{
            return data.category === Filtercateg
    }
    const reset =() =>{
        Setfiltercateg("")
        SetSubforcat("")
    }
    const Subcategories = props.SubCategories.filter(subcatfilter)
    return(
        <>
                 <div><ViewLayout/></div>

         <div className="Apps">
            <div className="heading"><h1>IEEE Projects</h1></div>
              <div class="head">
            {Projects.map((product)=>(<div className="card main" key={product._id}>
                (<div className="video"><iframe width="100%" height="200px" src={product.video} title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" referrerpolicy="strict-origin-when-cross-origin" allowfullscreen></iframe>
                <div className="titles" onClick={()=>(SetDesc(false))}><h4 className="card-title">{product.title}</h4></div>
                </div>
                <div className="content" width="100%">
                <div className="titles"><h4 className="card-title">{product.title}</h4></div>
                <div className="card-body">{product.description}</div>
        <button className="btn btn-info" onClick={()=>getabstract(`http://localhost:5000/view/files/${product.file_upload}`,product.title)}>Abstract</button>
        </div>)}
  </div>))}</div></div>
            
            
            
            
            
            
            
            
            {/* // Data.map((product)=>(
            //     <tr key={product._id}>
            //     <td>{product.title}</td>
            //     <td>{product.category}</td>
            //     <td>{product.sub_category}</td>
            //     <td>{product.description}</td>
                
            //     {product.file_type==="image/jpeg"?(<td><img src={`http://localhost:5000/view/files/${product.file_upload}`} alt={product.file_upload}/></td>):(<td><button onClick={()=>getabstract(`http://localhost:5000/view/files/${product.file_upload}`,product.title)}>Abstract</button></td>)}
            //     <td>{product.video}</td>                
            //     </tr>
            // ))} */}
        </>
    )
}
export default View;
