import { useState } from 'react'
import './static/Post.css'

const Post = (props) =>{
    const [Products,SetProducts] = useState([]) 
    const [Product,SetProduct] = useState({title:"",category:"",subcategory:"",description:"",fileupload:"",video:""})
    const handleInputChange =(event)=>{
        Setdata({...Data,[event.target.name]:event.target.value})
      };
      const handlephotoChange = (event)=>{
        Setdata({...Data,caption: event.target.files[0]})
      }
      
    
    return(
        <>
        <div className="form">
        <form>
            <div class="mb-3 mt-3">
               <label for="title" className="form-label">Title:</label>
                <input type="text" name="title" placeholder="Project Title" className="form-control"/>
                </div>
                <div class="mb-3 mt-3">
                <label for="category" className="form-label">Category:</label>
                <select name="category" className='form-control'>
                {props.Categories.map((category) => (
                <option key={category._id} value={category.category}>{category.category}</option>
                ))}
                </select>
                </div>
                <div class="mb-3 mt-3">
               <label for="subcategory" className="form-label">Sub-category:</label>
               <select name="subcategory" className='form-control'>
                {props.SubCategories.map((subcategory) => (
                <option key={subcategory._id} value={subcategory.subcategory}>{subcategory.subcategory}</option>
                ))}
                </select>
                </div>
                <div class="mb-3 mt-3">
               <label for="description" className="form-label">Description:</label>
                <textarea name="description" placeholder="description" className="form-control"/>
                </div>
                <div class="mb-3 mt-3">
               <label for="fileupload" className="form-label">Files:</label>
                <input type="file" name="fileupload" placeholder="Project files" className="form-control"/>
                </div>
                <div class="mb-3 mt-3">
               <label for="video" className="form-label">Video:</label>
                <input type="text" name="video" placeholder="Project video" className="form-control"/>
                </div>
                </form>
        </div>
        </>
    )
}
export default Post;
