import axios from "axios"
import React,{ useEffect, useState } from "react";

const View = () =>{
    const [Data,SetData] = useState([])
    async function fetchProduct(){
        const response = await axios.get("http://localhost:5000/view/view_product");
        SetData(response.data)
    }
    useEffect(()=>{
        fetchProduct()
    },[])
    const getabstract = () =>{
        fetch("http://localhost:5000/view/files/1717513846993.pdf").then((response)=>{
            response.blob().then((blob)=>{
                const fileURL = window.URL.createObjectURL(blob);
                let alink = document.createElement("a");
                alink.href = fileURL;
                alink.download = "SamplePDF.pdf";
                alink.click();
            })
        })
        
    }
    return(
        <>
        <table>
            <tr><th>Title</th><th>Category</th><th>Sub Category</th><th>Abstract</th><th>Files</th><th>Video</th></tr>
            {Data.map((product)=>(
                <tr key={product._id}>
                <td>{product.title}</td>
                <td>{product.category}</td>
                <td>{product.sub_category}</td>
                <td>{product.description}</td>
                
                {product.file_type==="image/jpeg"?(<td><img src={`http://localhost:5000/view/files/${product.file_upload}`} alt={product.file_upload}/></td>):(<td><button onClick={()=>getabstract(`http://localhost:5000/view/files/${product.file_upload}`)}>Abstract</button></td>)}
                <td>{product.video}</td>                
                </tr>
            ))}
        </table>
        </>
    )
}
export default View;
