const Categ = ()=>{
    const [Category,SetCategory] = useState("")
    const handleCategory = () =>{
        SetChange(false);
    }
    const handleCategoryinput = (event)=>{
        SetCategory(event.target.value)
    }
    return(

        <><div class="mb-3 mt-3">
               <label for="category" className="form-label">Category:</label>{Change?(<><select className='form-select'>
                {Categories.map((Categ)=>(
                    <option>{Categ}</option>
                ))}
                </select>
                <button type='button' onClick={handleCategory}>Add Category</button></>
                ):(
                <><form onSubmit={event=> {
                    event.preventDefault();
                    SetCategories([...Categories,Category])
                    SetCategory("")
                    SetChange(true)

                }}>
                <input type='text' name='cat' value={Category} onChange={handleCategoryinput}/><button type='submit'>Add</button></form></>
            )}                </div>
            </>
)
}

export default Categ;
