import { useEffect, useState } from 'react';
import './static/Post.css'
import axios from 'axios';
const Post = () =>{
    const [Categories,SetCategories] = useState([])
    const fetchcateg =()=>{
        const res = axios.get("http://localhost:5000/category/view_category");
        const data = res.data;
        SetCategories(data);
    }
    useEffect(()=>{
        fetchcateg()
    },[])
    
    return(
        <>
        <div className="form">
        {Categories}
        <form>
            <div class="mb-3 mt-3">
               <label for="title" className="form-label">Title:</label>
                <input type="text" name="title" placeholder="Project Title" className="form-control"/>
                </div>
                <div class="mb-3 mt-3">
               <label for="category" className="form-label">Category:</label>
                {(<><select className='form-select'>
                {Categories.map((Categ)=>(
                    <option>{Categ}</option>
                ))}
                </select>
                <button type='button' onClick={handleCategory}>Add Category</button></>
                )}
                </div>
                <div class="mb-3 mt-3">
               <label for="title" className="form-label">Title:</label>
                <input type="text" name="title" placeholder="Project Title" className="form-control"/>
                </div>
                <div class="mb-3 mt-3">
               <label for="title" className="form-label">Title:</label>
                <input type="text" name="title" placeholder="Project Title" className="form-control"/>
                </div>
                <div class="mb-3 mt-3">
               <label for="title" className="form-label">Title:</label>
                <input type="text" name="title" placeholder="Project Title" className="form-control"/>
                </div>
                <div class="mb-3 mt-3">
               <label for="title" className="form-label">Title:</label>
                <input type="text" name="title" placeholder="Project Title" className="form-control"/>
                </div>
                </form>
        </div>
        </>
    )
}
export default Post;
