import axios from "axios"
import React,{ useEffect, useState } from "react";
import './static/View.css'
const View = (props) =>{
    const [Data,SetData] = useState([])
    const [Filtercateg,Setfiltercateg] = useState("")
    const [Subforcat,SetSubforcat] = useState("")
    async function fetchProduct(){
        const response = await axios.get("http://localhost:5000/view/view_product");
        SetData(response.data)
    }
    useEffect(()=>{
        fetchProduct()
    },[])

    const getabstract = (urls,titles) =>{
        fetch(urls).then((response)=>{
            response.blob().then((blob)=>{
                const fileURL = window.URL.createObjectURL(blob);
                let alink = document.createElement("a");
                alink.href = fileURL;
                alink.download = titles + " abstract";
                alink.click();
            })
        })
    }
    const changecateg =(categ)=>{
        SetSubforcat("")
        Setfiltercateg(categ)
    }
    const changesubcat =(subcateg) =>{
        SetSubforcat(subcateg)
    }
    const filtering = (product) =>{
        return Filtercateg === "" ? 
    product.category.length > 0 : 
    (Subforcat === "" ? product.category === Filtercateg : product.sub_category === Subforcat);
    }
    const Projects = Data.filter(filtering)
    
    const subcatfilter = (data) =>{
            return data.category === Filtercateg
    }
    const reset =() =>{
        Setfiltercateg("")
        SetSubforcat("")
    }
    const Subcategories = props.SubCategories.filter(subcatfilter)
    return(
        <>
        <p><button onClick={()=>reset()}>No filter</button></p>
        <p>{
        props.Categories.map((cat)=>(<button type="button" onClick={()=>changecateg(cat.category)} key={cat._id}><li>{cat.category}</li></button>))}</p>
        <p>{
        Subcategories.map((subcat)=>(<button type="button" onClick={()=>changesubcat(subcat.subcategory)} key={subcat._id}><li>{subcat.subcategory}</li></button>))}</p>
        <div className="Apps container-fluid">
              <div class="head row">
            {Projects.map((product)=>(<div className="card main" key={product._id}>
                <div className="titles"><h4 className="card-title">{product.title}</h4></div>
                <iframe width="100%" height="200px" src={product.video} title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" referrerpolicy="strict-origin-when-cross-origin" allowfullscreen></iframe>                <div className="card-body">
      <div width="100%"><button className="btn btn-info" onClick={()=>getabstract(`http://localhost:5000/view/files/${product.file_upload}`,product.title)}>Abstract</button></div>
    </div>
  </div>))}</div></div>
            
            
            
            
            
            
            
            
            {/* // Data.map((product)=>(
            //     <tr key={product._id}>
            //     <td>{product.title}</td>
            //     <td>{product.category}</td>
            //     <td>{product.sub_category}</td>
            //     <td>{product.description}</td>
                
            //     {product.file_type==="image/jpeg"?(<td><img src={`http://localhost:5000/view/files/${product.file_upload}`} alt={product.file_upload}/></td>):(<td><button onClick={()=>getabstract(`http://localhost:5000/view/files/${product.file_upload}`,product.title)}>Abstract</button></td>)}
            //     <td>{product.video}</td>                
            //     </tr>
            // ))} */}
        </>
    )
}
export default View;
