import axios from "axios"
import React,{ useEffect, useState } from "react";

const View = () =>{
    const [Data,SetData] = useState([])
    async function fetchProduct(){
        const response = await axios.get("http://localhost:5000/view/view_product");
        SetData(response.data)
    }
    useEffect(()=>{
        fetchProduct()
    },[])
    const getabstract = (urls,titles) =>{
        fetch(urls).then((response)=>{
            response.blob().then((blob)=>{
                const fileURL = window.URL.createObjectURL(blob);
                let alink = document.createElement("a");
                alink.href = fileURL;
                alink.download = titles + " abstract";
                alink.click();
            })
        })
        
    }
    return(
        <>
            {Data.map((product)=>(<div className="card main" key={product._id}>
    <img className="card-img-top image" alt="hello" src="logo192.png"/>
    <div className="card-body">
    <h4 className="card-title">{product.title}</h4>
      <p className="card-text">{product.description}</p>
      <button className='btn btn-warning'>Add to Cart</button>
    </div>
  </div>))}
            
            
            
            
            
            
            
            
            {/* // Data.map((product)=>(
            //     <tr key={product._id}>
            //     <td>{product.title}</td>
            //     <td>{product.category}</td>
            //     <td>{product.sub_category}</td>
            //     <td>{product.description}</td>
                
            //     {product.file_type==="image/jpeg"?(<td><img src={`http://localhost:5000/view/files/${product.file_upload}`} alt={product.file_upload}/></td>):(<td><button onClick={()=>getabstract(`http://localhost:5000/view/files/${product.file_upload}`,product.title)}>Abstract</button></td>)}
            //     <td>{product.video}</td>                
            //     </tr>
            // ))} */}
        </>
    )
}
export default View;
