import { useState } from 'react';
import './static/Post.css'
const Post = () =>{
    const [Category,SetCategory] = useState("")
    const [Categories,SetCategories] = useState([])
    const [Change,SetChange] = useState(true);
    const handleCategory = () =>{
        SetChange(false);
    }
    const handleCategoryinput = (event)=>{
        SetCategory(event.target.value)
    }
    return(
        <>
        <div className="form">
        {Category}
        
            <div class="mb-3 mt-3">
               <label for="title" className="form-label">Title:</label>
                <input type="text" name="title" placeholder="Project Title" className="form-control"/>
                </div>
                <div class="mb-3 mt-3">
               <label for="category" className="form-label">Category:</label>
                {Change?(<><select className='form-select'>
                <option>ML</option>
                <option>Django</option>
                <option>Python</option>
                </select>
                <button type='button' onClick={handleCategory}>Add Category</button></>
                ):(
                <><form onSubmit={event=> {
                    event.preventDefault();
                    SetCategories(Category)
                    SetCategory("")
                    SetChange

                }}>
                <input type='text' name='cat' value={Category} onChange={handleCategoryinput}/><button type='submit'>Add</button></form></>)}
                </div>
                <div class="mb-3 mt-3">
               <label for="title" className="form-label">Title:</label>
                <input type="text" name="title" placeholder="Project Title" className="form-control"/>
                </div>
                <div class="mb-3 mt-3">
               <label for="title" className="form-label">Title:</label>
                <input type="text" name="title" placeholder="Project Title" className="form-control"/>
                </div>
                <div class="mb-3 mt-3">
               <label for="title" className="form-label">Title:</label>
                <input type="text" name="title" placeholder="Project Title" className="form-control"/>
                </div>
                <div class="mb-3 mt-3">
               <label for="title" className="form-label">Title:</label>
                <input type="text" name="title" placeholder="Project Title" className="form-control"/>
                </div>
                
        </div>
        </>
    )
}
export default Post;
