import axios from "axios"
import React,{ useEffect, useState } from "react";
import './static/View.css'
const View = (props) =>{
    const [Data,SetData] = useState([])
    async function fetchProduct(){
        const response = await axios.get("http://localhost:5000/view/view_product");
        SetData(response.data)
    }
    useEffect(()=>{
        fetchProduct()
    },[])
    const getabstract = (urls,titles) =>{
        fetch(urls).then((response)=>{
            response.blob().then((blob)=>{
                const fileURL = window.URL.createObjectURL(blob);
                let alink = document.createElement("a");
                alink.href = fileURL;
                alink.download = titles + " abstract";
                alink.click();
            })
        })
        
    }
    return(
        <><div className="Apps container-fluid">
            <p>{props.Categories.map((cat)=>(<ul key=""><li></li></ul>))}</p>
              <div class="head">
            {Data.map((product)=>(<div className="card main" key={product._id}>
                <div className="titles"><h4 className="card-title">{product.title}</h4></div>
                <iframe width="100%" height="200px" src="https://www.youtube.com/embed/AJZJgrmr4bQ?si=D1-vj021lyMx6e1R" title="YouTube video player" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" referrerpolicy="strict-origin-when-cross-origin" allowfullscreen></iframe>                <div className="card-body">
      <div width="100%"><button className="btn btn-info" onClick={()=>getabstract(`http://localhost:5000/view/files/${product.file_upload}`,product.title)}>Abstract</button></div>
    </div>
  </div>))}</div></div>
            
            
            
            
            
            
            
            
            {/* // Data.map((product)=>(
            //     <tr key={product._id}>
            //     <td>{product.title}</td>
            //     <td>{product.category}</td>
            //     <td>{product.sub_category}</td>
            //     <td>{product.description}</td>
                
            //     {product.file_type==="image/jpeg"?(<td><img src={`http://localhost:5000/view/files/${product.file_upload}`} alt={product.file_upload}/></td>):(<td><button onClick={()=>getabstract(`http://localhost:5000/view/files/${product.file_upload}`,product.title)}>Abstract</button></td>)}
            //     <td>{product.video}</td>                
            //     </tr>
            // ))} */}
        </>
    )
}
export default View;
